function getDaysOnMeter() {
   var fm = globe.cookie.get('FM');
   if (!fm) return;
   var startDateString = fm.split(':')[0].replace(/\"/g, ''), // see BGLOBE-3644 for replace reason
       year = parseInt(startDateString.substr(0,4),10),
       month = parseInt(startDateString.substr(4,2),10) - 1;
       day = parseInt(startDateString.substr(6,2),10),
       startDate = new Date(year, month, day),
       today = new Date();
       oneDay = 1000*60*60*24,
       delta = today.getTime() - startDate.getTime(),
       daysOnMeter = Math.ceil(delta / oneDay) ;
      
  return 'Day ' + daysOnMeter;
  
}
