(function(win, $, undefined){

    // Build lookup from the globe.dfp.adCatalog table
    var adKeys = [];
    for (var i in globe.dfp.adCatalog) {
        var obj = globe.dfp.adCatalog[i];

        adKeys[obj.DFPPosition] = {};
        adKeys[obj.DFPPosition].pos = obj.pos || false;
        adKeys[obj.DFPPosition].width = obj.width || "100%";
        adKeys[obj.DFPPosition].height = obj.height || "250";
    }

    //this can be called to initialize ad divs that do not yet have ads appended
    //call on DomReady
    globe.dfp.loader = function() {
        if ( !globe.dfp.adUnit ) { return; }
        
        // Strip www. from adUnit / This feels wrong
        var www = globe.dfp.adUnit.indexOf('www.');
        if ( www === 0 ) { globe.dfp.adUnit = globe.dfp.adUnit.replace('www.', ''); }
        //grabs all ads with class of 'ad' with data-adname attribue
        var $ads = $( ".ad:not([data-adname-complete])" );
        var iAds = $ads.length;
        var networkCode = 61381659;
        var activeAds = [];

      // Initial loop to set up the ads / remove unused DFP slots
        for ( var i = 0; i < iAds; i++ ) {

            var $thisAd = $( $ads[i] );
            var DFPPosition = $thisAd.attr("id");

            // Is there a registered ad with this position name?
            if ( adKeys[DFPPosition] ) {

                // Is there a corresponding DFP position?
               var adWidth = parseInt(adKeys[DFPPosition].width, 10);
               var adHeight = parseInt(adKeys[DFPPosition].height, 10);
               //dfp specific; pushing commands into array
               googletag.cmd.push(function() {
                  googletag.defineSlot("/" + networkCode + "/" + globe.dfp.adUnit , [adWidth,adHeight],  DFPPosition)
                     .addService(googletag.pubads())
                     .setTargeting("pos", adKeys[DFPPosition].pos);
               });

              // Set attr to prevent re-running on this elem
              $thisAd.attr("data-adname-complete", true);

              // Always load ads in the drawer. Otherwise, use the visibility check
              if ( $thisAd.is( ":visible" ) || $thisAd.parents( ".saved-panel" ).length ) {
                  activeAds.push(DFPPosition);
              } else {
                  $thisAd.empty();    // Defensive cat is defensive
              }
            }
        }

      googletag.cmd.push(function() {
         googletag.pubads().enableSingleRequest();
         //we are now ready to display ads
         googletag.enableServices();
      });

      // Check test query string
      globe.dfp.keyValuePairs.test = getQueryStringValue('test');

      // Loop throught globe.dfp.keyValuePairs object to add targeting
      var kvPairs = globe.dfp.keyValuePairs;
      for ( var key in kvPairs ) {
         googletag.cmd.push(function() {
            googletag.pubads().setTargeting( key, kvPairs[key] );
         }); 
      }

      // Loop to display the ads
      for ( var i = 0; i < activeAds.length; i++ ) {
         googletag.cmd.push(function() {
            googletag.display(activeAds[i]);
         });
      }

    };

    
    /*
       Read RDB cookie and add appropriate values to globe.dfp.keyValuePairs
       Dummy cookie is set on doc ready for testing purposes
       Maybe clean this code up at some point?
    */
    // Functions that either get or set a cookie
    // Checks for the presence of the RDB cookie
    function checkCookie() {
       var rdb = getCookie('RDB');
       if (rdb != null && rdb != '') {
          //if the cookie exists hit up the cookieParser to get our data
          cookieParser(rdb);
       } else {
          return false;
       }
    }
    function getCookie(c_name) {
       var i, x, y, ARRcookies = document.cookie.split(";");
       for (i = 0; i < ARRcookies.length; i++) {
          x = ARRcookies[i].substr(0, ARRcookies[i].indexOf("="));
          y = ARRcookies[i].substr(ARRcookies[i].indexOf("=") + 1);
          x = x.replace(/^\s+|\s+$/g, "");
          if (x == c_name) {
             return unescape(y);
          }
       }
    }
    // A more complex hex to ascii translation - useful in a number of places especially when you need letters back
    function translator(hex) {
       var arr = hex.match(/../g);
       var response = '';
       for (i = 0; i < arr.length; i++) {
          var m = parseInt(arr[i], 16);
          var t = String.fromCharCode(m);
          response = response + t;
       }
       return response;
    }
    // Generic Decimal to Hex Conversion
    function d2h(d) {
       return d.toString(16);
    }
    // Generic Hex to Decimal Conversion
    function h2d(h) {
       return parseInt(h, 16);
    }
    // Specific functions for messing with the cookie input to readable output
    // Specific function to check the zip code. If the zip has less than 4 characters than we need add a '0' as the cause for this issue is that the leading '0' is dropped.
    function zip5checker(hex) {
       var z = parseInt(hex, 16).toString();
       if (z.length > 4) {
          return z;
       } else {
          return '0' + z;
       }
    }
    // Like your pet turtle - this flips the hex over and checks for gender
    function genderChecker(hex) {
       var response = '';
       switch (hex) {
       case '01':
          response = 'm';
          break;
       case '02':
          response = 'f';
          break;
       default:
          response = '';
          break;
       }
       return response;
    }
    function age(low, high) {
       var age, date, curYear, l, h, range;
       date = new Date();
       curYear = date.getFullYear();
       l = (curYear - h2d(low) + 1);
       h = (curYear - h2d(high) - 1);
       if (l == h) {
          return range = curYear - l;
       } else {
          range = curYear - l;
          range += '-';
          range += curYear - h;
          return range;
       }
    }
    function toUnicode(theString) {
       var unicodeString = '';
       var theUnicode = theString;
       while (theUnicode.length < 4) {
          theUnicode = '0' + theUnicode;
       }
       theUnicode = '\\x' + theUnicode;
       unicodeString += theUnicode;
       return unicodeString;
    }
    function cookieParser(cookie) {
       // obj container for freeback to Lotame
       var obj = new Object;
       obj = {
          zip5: zip5checker(cookie.substring(4, 10)),
          gender: genderChecker(cookie.substring(48, 50)),
          age: age(cookie.substring(56, 58), cookie.substring(58, 60)),
       };
       //this checks to make sure we don't have an empty object and then dispatches the info out to lotame.
       if (!$.isEmptyObject(obj)) {
          globe.dfp.keyValuePairs.age = obj.age;
          globe.dfp.keyValuePairs.zip = obj.zip5;
          globe.dfp.keyValuePairs.g = obj.gender;
       };
       return obj;
    }
    // Parse query string values
    function getQueryStringValue( key ) {
        var queryStringArray = window.location.search.substr( 1 ).split( '&' );
        for ( var i = 0; i < queryStringArray.length; i++ ) {
            var keyValueArray = queryStringArray[i].split( '=' );
            if ( keyValueArray[0] === key ) {
                return keyValueArray[1];
            }
        }
        return false;
    }
    
    function addAAMValuesToKeyValuePairs(){
      var aam_dfp   = globe.cookie.get("aam_dfp"),          
          aam       = aam_dfp ? decodeURIComponent(aam_dfp).split(',')[0] : '',
          aam_value = aam ? aam.split('=')[1]: '';
      globe.dfp.keyValuePairs.aam = aam_value;
      globe.dfp.keyValuePairs.aam_uuid = globe.cookie.get("aam_uuid");
    }

    // Check RDB cookie
    checkCookie();
    addAAMValuesToKeyValuePairs();
    
    //run dfp loader on domready    
    $( globe.dfp.loader );

})(window, jQuery);

